export default (state,action)=>{
    switch(action.type){
        case "Delete":
            return{
                ...state,
                transactions:state.transactions.filter(transactions => transactions.id!==action.payload)
            }
        case "Add":
            return{
                ...state,
                transactions:[...state.transactions,action.payload]
            }
        default:
          return state;
    }
}
